mapper.select:
select cust_comp_nm as custcompnm , cpsn_nm as cpsnnm , cpsn_tel_no as cpsntelno , cust_comp_memo_ctt as custcompmemoctt , cpsn_email as cpsnemail , stm_frst_reg_dtm as stmfrstregdtm , stm_frst_reg_user_id as stmfrstreguserid , stm_last_mod_user_id as stmlastmoduserid from cust_comp where 1 = 1
and stm_frst_reg_dtm between #{startDate} and #{endDate}
and cust_comp_id like CONCAT('%' , #{custCompId} , '%')
and cust_comp_nm like CONCAT('%' , #{custCompNm} , '%')
and cpsn_nm like CONCAT('%' , #{cpsnNm} , '%')
order by stm_frst_reg_dtm desc limit #{pageStart}, #{perPageNum}

mapper.insert:
insert into cust_comp( cust_comp_id
                     , cust_comp_nm
                     , cpsn_nm
                     , cpsn_tel_no
                     , cust_comp_memo_ctt
                     , cpsn_email
                     , STM_FRST_REG_USER_ID
                     , STM_FRST_REG_DTM
                     , STM_LAST_MOD_USER_ID
                     , STM_LAST_MOD_DTM)
values ( (SELECT NEXTVAL('my_sequence'))
       , #{custCompNm}
       , #{cpsnNm}
       , #{cpsnTelNo}
       , #{custCompMemoCtt}
       , #{cpsnEmail}
       , #{stmFrstRegUserId}
       , NOW()
       , #{stmLastModfUserId}
       , NOW())
